import React, { useEffect, useState } from "react";
import "../index.scss";
import axios from "axios";

const AllRecepies = () => {
  const [data, setData] = useState([]);

  const fetch = () => {
    axios
      .get("http://localhost:4000/api/AllRecepies")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    fetch();
  }, []);

  {
    data.map((e) => {
      return (
        <div className="card-container">
          <div className="card">
            <button className="delete" onClick={console.log(e)}>
              delete
            </button>
            <button className="update">update </button>
            <>
              <div className="header">
                <img className="img" src={e.recepie_Image} alt="food" />
              </div>
              <div className="text">
                <h1 className="food">{e.recepie_Name}</h1>
                <i> {e.Cook_Time}</i> <br />
                <i> {e.Serves} </i>
              </div>
            </>
          </div>
        </div>
      );
    });
  }
};

export default AllRecepies;
