import React, { useEffect, useState } from "react";
import axios from "axios";
import "../index.scss";

const AllRecipes = () => {
  const [selectedRecipeId, setSelectedRecipeId] = useState("");
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = async () => {
    try {
      const response = await axios.get("http://localhost:4000/api/recipes");
      setRecipes(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleDelete = async (recipeId) => {
    try {
      await axios.delete(`http://localhost:4000/api/recipes/${recipeId}`);
      fetchRecipes();
    } catch (error) {
      console.log(error);
    }
  };

  const handleUpdate = async (recipeId) => {
    try {
      await axios.put(`http://localhost:4000/api/recipes/${recipeId}`);
      fetchRecipes();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="card-container">
      {recipes.map((recipe) => (
        <div className="card" key={recipe.id}>
          <button className="delete" onClick={() => handleDelete(recipe.id)}>
            Delete
          </button>
          <button
            className="update"
            onClick={() => setSelectedRecipeId(recipe.id)}
          >
            Update
          </button>
          <div className="header">
            <img className="img" src={recipe.image} alt="food" />
          </div>
          <div className="text">
            <h1 className="food">{recipe.name}</h1>
            <i>{recipe.cookTime}</i> <br />
            <i>{recipe.serves}</i>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AllRecipes;
