import React, { useEffect, useState } from "react";
import axios from "axios";
import "../index.scss";
import Update from "./update";

const AllRecipes = ({ changeView, view }) => {
  // const [selected, setSelectedRecipeId] = useState("");
  const [recipes, setData] = useState([]);
  const [upd, setUpd] = useState([]);

  const fetch = () => {
    axios
      .get("http://localhost:4000/api/AllRecepies")
      .then((response) => {
        setData(response.data);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    fetch();
  }, []);

  const handleDelete = (Id) => {
    axios
      .delete(`http://localhost:4000/api/Recepie/${Id}`)
      .then((res) => fetch())
