      .then((res) => fetch())
      .catch((error) => console.log(error));
  };
  const hanlde = (i) => {
    setUpd(i);
    changeView("");
  };

  return (
    <div className="card-container">
      {recipes.map((e) => (
        <div className="card" key={e.recepie_Id}>
          <button className="delete" onClick={() => handleDelete(e.recepie_Id)}>
            Delete
          </button>

          <button className="update" onClick={() => changeView("update")}>
            Update
          </button>
          <div className="header">
            <img className="img" src={e.recepie_Image} alt="food" />
          </div>
          <div className="text">
            <h1 className="food">{e.recepie_Name}</h1>
            <i>time to cook {e.Cook_Time}</i> <br />
            <i>time to serve {e.Serves}</i>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AllRecipes;
